import {useState,useEffect} from 'react';
import BlogList from  './BlogList'
const Home = () => {
    const [blogs, setblogs] = useState(null);
    const [isPending, setisPending] = useState(true);
    const [error, seterror] = useState(null);
    const [temp, settemp] = useState(0);
    const incrementVar = () =>{
        settemp(temp+1);
    }
    const decrementVar = () =>{
        settemp(temp-1);
    }
    useEffect(() => {
        setTimeout(()=>{
            fetch('http://localhost:8000/blogs')
            .then((response) => {
                if(!response.ok){
                    throw Error('could not fetch data from server')
                }
                return response.json()
            })
            .then((data) =>{
                setblogs(data);
                setisPending(false);
                seterror(null);
            })
            .catch((err) =>{
                seterror(err.message);
                setisPending(false);
            })
        },1000)
    },[]);
    return (
        <div className="home">
        {error && <div>{error}</div>}
        {isPending && !error && <div>Loading...</div>}
        {blogs && !error && <BlogList blogs={blogs} title="All Blogs!" ></BlogList>}
        <button onClick = {(temp)=>incrementVar(temp)}>Increment</button>
        <button onClick = {(temp)=>decrementVar(temp)}>Decrement</button>
        <p>{temp}</p>
        </div>
    );
}

export default Home
