import {useState,useEffect} from 'react';
import BlogList from  './BlogList'
const Home = () => {
    const [blogs, setblogs] = useState([
        {title:'My new website',body:'lorem',author:'mario',id:1},
        {title:'Welcome',body:'lorem',author:'yoshi',id:2},
        {title:'Hello',body:'lorem',author:'luigi',id:3}
    ]);
    const handleDelete = (id)=>{
        const newBlogs = blogs.filter(blog => blog.id !==id);
        setblogs(newBlogs);
    }
    const [name,setname] = useState('mario');
    useEffect(() => {
        console.log('use effect ran');
    },[name]);
    const alternateName = (name) =>{
        if(name === 'mario'){
            setname('luigi');
        }
        else{
            setname('mario')
        }
    }
    return (
        <div className="home">
           <BlogList blogs={blogs} title="All Blogs!" handleDelete={handleDelete}></BlogList>
           <button onClick={alternateName}>change name</button>
           <p>{name}</p>
        </div>
    );
}

export default Home
