import { useParams } from "react-router";
import {Link} from 'react-router-dom';

const BlogDetails = () => {
    const {id}=useParams();
    return (<div className="blog-details">
    <h2>Blog Details - {id}</h2>
    <input type="search" className="searchBar"></input>
        <Link to="/blogs/:id">
        <button>Hello!</button>
    </Link>
    </div>);
}

export default BlogDetails;
