import BlogList from  './BlogList'
import useFetch from './useFetch';
import {Link} from 'react-router-dom';

const Home = () => {
    const {data:blogs, isPending, error} = useFetch('http://localhost:8000/blogs');

    return (
        <div className="home">
        {error && <div>{error}</div>}
        {isPending && !error && <div>Loading<p className="loading"></p></div>}
        {blogs && !error && <BlogList blogs={blogs } title="All Blogs!" ></BlogList>}
        <input type="search" className="searchBar" id='yolo'></input>
        <Link to={`/blogs/` + `${document.getElementById('yolo').value}`}>
        <button>Hello!</button>
        </Link>
        </div>
    );
}

export default Home
